import { useEffect, useState } from 'react';

const Auth = () => {

    let [login, setlogin] = useState(false);
    //<p>Hai già un account? <a onClick={setlogin(true)}>Accedi qui</a></p>
    //<p>Non hai un account? <a onClick={setlogin(false)}>Registrati ora</a></p>

    if(!login)
        return (
            <div className="container">
                <h1>Registrazione</h1>
                <form onSubmit={handleRegister}>
                    <input type="text" name="nome" placeholder="Nome utente" required/>
                    <input type="text" name="cognome" placeholder="Cognome utente" required/>
                    <input type="email" name="email" placeholder="Email" required/>
                    <input type="password" name="password" placeholder="Password" required/>
                    <input type="password" name="confirm-password" placeholder="Conferma Password" required/>
                    <input type="submit" value="Registrati"/>
                </form>

            </div>
        );
    else return (
        <div class="container">
            <h1>Accedi / Registrati</h1>
            <form onSubmit={handleLogin}>
                <input type="text" name="username" placeholder="Nome utente" required/>
                <input type="password" name="password" placeholder="Password" required/>
                <input type="submit" value="Accedi"/>
            </form>
        </div>
        );
}

export default Auth;

const api_url = 'http://localhost:8080/';

const handleLogin = (event) => {
    event.preventDefault();
    let user = {};
    user["email"] = event.target.email.value;
    user["password"] = event.target.password.value;
    sendLogin(user)
        .then(response => response.json())
        .then(data => console.log(data))
        .catch(error => console.error('Error:', error));
}

const handleRegister = (event) => {
    event.preventDefault();
    let user = {};
    user["nome"] = event.target.nome.value;
    user["cognome"] = event.target.cognome.value;
    user["email"] = event.target.email.value;
    user["password"] = event.target.password.value;
    sendRegister(user)
        .then(response => response.json())
        .then(data => console.log(data))
        .catch(error => console.error('Error:', error));
}

const sendLogin = (user) => {
    let res = fetch(api_url+'auth/login/', {
        method: 'POST',
        mode: 'cors',
        body: JSON.stringify(user)
    })

    return res;
}

const sendRegister = (user) => {
    let res = fetch(api_url+'auth/register/', {
        method: 'POST',
        mode: 'cors',
        body: JSON.stringify(user)
    })

    return res;
}
