import React from "react";
import { useState } from "react";
import "./App.css";

export default function NewBookRow(probs) {
  const [ISBN, setISBN] = useState("");
  const [titolo, setTitolo] = useState("");
  const [autore, setAutore] = useState("");
  const [trama, setTrama] = useState("");
  const [letture, setLetture] = useState(0);

  const onSubmit = (e) => {
    e.preventDefault();
    let newBook = { name: ISBN, email: titolo, status: autore, trama: trama, letture: letture };

    fetch("http://localhost:8080/delete-book?Authorization="+token, {
      method: "POST",
      body: JSON.stringify(newBook),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        probs.deleteUser(probs.id);
        probs.updateBook({
          id: data,
          name: ISBN,
          email: titolo,
          status: autore,
          viewMode: true,
        });
      })
      .catch(() => {
        console.log("Somthing failed");
      });
  };

  return (
    <form onSubmit={onSubmit}>
      <div className="NewUser">
        <div className="InputBox">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            onChange={(event) => setISBN(event.target.value)}
          />
        </div>
        <div className="InputBox">
          <label>Email:</label>
          <input
            type="email"
            name="email"
            onChange={(event) => setTitolo(event.target.value)}
          />
        </div>
        <div className="InputBox">
          <label>Autore:</label>
          <input
            type="text"
            name="autore"
            onChange={(event) => setAutore(event.target.value)}
          />
        </div>
        <div className="InputBox">
          <label>Autore:</label>
          <input
            type="text"
            name="autore"
            onChange={(event) => setAutore(event.target.value)}
          />
        </div>
        <div className="InputBox">
          <label>Autore:</label>
          <input
            type="text"
            name="autore"
            onChange={(event) => setAutore(event.target.value)}
          />
        </div>
        <button type="submit">Submit</button>
        <button onClick={() => probs.deleteUser(probs.id)}>Delete</button>
      </div>
    </form>
  );
}
