import { useEffect, useState } from 'react';

const Overview = ({ token }) => {

    const [books, setBooks] = useState([]);
    const [newBooks, setNewBooks] = useState([]);

    useEffect(() => {
        fetch("http://localhost:8080/books?Authorization="+token, {
            method: "GET",
            headers: {
                'Authorization': token,
                'Content-Type': 'application/json'
            }
        }).then(response => response.json())
            .then(data => {
                for(const book in data) book["view"] = true;
                setBooks(data);
            })
    },[]);

    function deleteBook(ISBN) {
        fetch("http://localhost:8080/delete-book?Authorization="+token+"&ISBN="+ISBN,{
          method: "DELETE",
          headers: {'Authorization': token}
        }).then(() => {
          setBooks((oldValues) => {
            return oldValues.filter((book) => book.ISBN !== ISBN);
          });
        });
      }

      function editModeChange(ISBN) {
        console.log("Changing edit mode");
        for (const element of books) {
          if (element.ISBN == ISBN) {
            element["viewMode"] = !element["viewMode"];
          }
        }
        setBooks((prevUsers) => [...prevUsers]);
      }

      function updateUser(ISBN, book) {
        fetch("http://localhost:8080/delete-book?Authorization="+token+"&ISBN="+ISBN, {
          method: "POST",
          headers: {'Authorization': token, 'Content-Type': 'application/json'},
          body: JSON.stringify(book),
        }).then(() => {
          for (const element of books)
            if (element.ISBN == ISBN) {
              for(const p in element)
                element[p] = book[p];
              editModeChange(element.ISBN);
              break;
            }
        });
      }

      function addNewUserInput() {
        setNewBooks((newBooks) => [...newBooks, {}]);
      }

      function deleteNewUser(ISBN) {
        setNewBooks((oldValues) => {
          return oldValues.filter((book) => book.ISBN !== ISBN);
        });
      }

      function updateNewUser(newBook) {
        setBooks([...books, newBook]);
      }

    return (
        <div className='container'>
            {books.map((book) => (
            <div key={book.ISBN}>
                <BookRow book={book}/>
            </div>
            ))}

            {newBooks.map((newISBN) => (
            <div key={newISBN}>
                <NewBook
                    id={newUserID}
                    deleteUser={deleteNewUser}
                    updateNewUser={updateNewUser}
                />
            </div>
            ))}
            <div>
                <button onClick={() => addNewBookInput()}>New User</button>
            </div>
        </div>
    );
}
export default Overview;


