import { useEffect, useState } from 'react';

const Auth = () => {

    let [login, setlogin] = useState(0);

    useEffect(() => setlogin(false), []);

    if(!login)
        return (
            <div className="container">
                <h1>Registrazione</h1>
                <form>
                    <input type="text" name="username" placeholder="Nome utente" required/>
                    <input type="email" name="email" placeholder="Email" required/>
                    <input type="password" name="password" placeholder="Password" required/>
                    <input type="password" name="confirm-password" placeholder="Conferma Password" required/>
                    <input type="submit" value="Registrati" onClick={sendRegister}/>
                </form>
            <p>Hai gi√† un account? <a href="/login">Accedi qui</a></p>
            </div>
        );
    else return (
        <div class="container">
            <h1>Accedi / Registrati</h1>
            <form>
                <input type="text" name="username" placeholder="Nome utente" required/>
                <input type="password" name="password" placeholder="Password" required/>
                <input type="submit" value="Accedi" onClick={handleLogin}/>
            </form>
            <p>Non hai un account? <a href="/register">Registrati ora</a></p>
        </div>
        );
}

export default Auth;

const api_url = 'http://backend:8080/';

const handleLogin = (event) => {
    console.log(sendLogin(event.target.email.value, event.target.password));
}

const handleregister = (event) => {
    console.log(sendLogin(event.target.nome.value, event.target.cognome.value, event.target.email.value, event.target.password));
}

const sendLogin = (email, password) => {
    let user = {
        "email": email,
        "password": password
    };

    let res = fetch(api_url+'auth/login/', {
        method: 'POST',
        mode: 'cors',
        body: JSON.stringify(user)
    })

    return res;
}

const sendRegister = (nome, cognome, email, password) => {
    let user = {
        "nome": nome,
        "cognome": cognome,
        "email": email,
        "password": password
    };

    let res = fetch(api_url+'auth/login/', {
        method: 'POST',
        mode: 'cors',
        body: JSON.stringify(user)
    })

    return res;
}
