import { useEffect, useState } from 'react';

const Auth = () => {
    let [login, setlogin] = useState(0);

    useEffect(() => setlogin(false));


    if(!login)
        return (
            <div className="container">
                <h1>Registrazione</h1>
                <form action="/path/to/register" method="post">
                    <input type="text" name="username" placeholder="Nome utente" required/>
                    <input type="email" name="email" placeholder="Email" required/>
                    <input type="password" name="password" placeholder="Password" required/>
                    <input type="password" name="confirm-password" placeholder="Conferma Password" required/>
                    <input type="submit" value="Registrati"/>
                </form>
            <p>Hai gi√† un account? <a href="/login">Accedi qui</a></p>
            </div>
        );
    else return (
        <div class="container">
            <h1>Accedi / Registrati</h1>
            <form action="/path/to/login" method="post">
                <input type="text" name="username" placeholder="Nome utente" required/>
                <input type="password" name="password" placeholder="Password" required/>
                <input type="submit" value="Accedi"/>
            </form>
            <p>Non hai un account? <a href="/register">Registrati ora</a></p>
        </div>
        );
}

export default Auth;
