import { useEffect, useState } from 'react';
import {setToken} from './App.js';

const Auth = () => {

    let [login, setlogin] = useState(true);

    if(!login)
        return (
            <div className="container">
                <h1>Registrati</h1>
                <form onSubmit={handleRegister}>
                    <input type="text" name="nome" placeholder="Nome utente" required/>
                    <input type="text" name="cognome" placeholder="Cognome utente" required/>
                    <input type="email" name="email" placeholder="Email" required/>
                    <input type="password" name="password" placeholder="Password" required/>
                    <input type="password" name="confirm-password" placeholder="Conferma Password" required/>
                    <input type="submit" value="Registrati"/>
                </form>
                <p>Hai gi√† un account? <a onClick={(e) => setlogin(true)}>Accedi qui</a></p>
            </div>
        );
    else return (
        <div className="container">
            <h1>Accedi</h1>
            <form onSubmit={handleLogin}>
                <input type="text" name="email" placeholder="Nome utente" required/>
                <input type="password" name="password" placeholder="Password" required/>
                <input type="submit" value="Accedi"/>
            </form>
            <p>Non hai un account? <a onClick={(e) => setlogin(false)}>Registrati ora</a></p>
        </div>
        );
}

export default Auth;

const handleLogin = (event) => {
    event.preventDefault();
    let user = {
        email: event.target.email.value, 
        password: event.target.password.value
    };
    fetch('http://localhost:8080/login', {
        method: 'POST',
        mode: 'cors',
        headers:{'Content-Type': 'application/json'},
        body: JSON.stringify(user)
    }).then(response => {
        if(response.ok) return response.text();
        else throw new Error(response.status)
    }).then(data => setToken(data))
        .catch(error => console.error('Error:', error));
}

const handleRegister = (event) => {
    event.preventDefault();
    let user = {
        nome: event.target.nome.value, 
        cognome: event.target.cognome.value, 
        email: event.target.email.value, 
        password: event.target.password.value
    };
    fetch('http://localhost:8080/register', {
        method: 'POST',
        headers:{'Content-Type': 'application/json'},
        mode: 'cors',
        body: JSON.stringify(user)
    }).then(response => response.json())
        .then(data => console.log(data))
        .then(setlogin(true))
        .catch(error => console.error('Error:', error));
}
