import React from "react";
import { useState } from "react";
import "./App.css";

export default function BookRow(book) {
  const [ISBN, setISBN] = useState(book.ISBN);
  const [titolo, setTitolo] = useState(book.titolo);
  const [autore, setAutore] = useState(book.autore);
  const [trama, setTrama] = useState(book.trama);
  const [letture, setLetture] = useState(book.letture);

  if (probs.user.viewMode) {
    return (
      <div className="User">
        <p style={{ margin: 20 }}>{ISBN}</p>
        <p style={{ margin: 20 }}>{titolo}</p>
        <p style={{ margin: 20 }}>{autore}</p>
        <p style={{ margin: 20 }}>{trama}</p>
        <button onClick={() => probs.deleteUser(probs.user)}>Delete</button>
        <button onClick={() => probs.editModeChange(probs.user.id)}>
          Edit
        </button>
      </div>
    );
  } else {
    return (
      <div className="User">
        <div className="NewUser">
          <p style={{ margin: 20 }}>{ISBN}</p>
          <div className="InputBox">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={titolo}
              onChange={(event) => setTitolo(event.target.value)}
            />
          </div>
          <div className="InputBox">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={autore}
              onChange={(event) => setAutore(event.target.value)}
            />
          </div>
          <div className="InputBox">
            <label>Status:</label>
            <input
              type="text"
              name="status"
              value={trama}
              onChange={(event) => setTrama(event.target.value)}
            />
          </div>
        </div>
        <button onClick={() => probs.updateUser(ISBN, titolo, autore, trama)}>
          Update
        </button>
        <button onClick={() => probs.editModeChange(probs.user.id)}>
          Restore
        </button>
      </div>
    );
  }
}
